<style>
    .comecar-page * { margin: 0; padding: 0; box-sizing: border-box; }

    .comecar-page {
        font-family: 'Georgia', 'Times New Roman', serif;
        background-color: #f7f5f0;
        color: #3d3630;
        line-height: 1.85;
        font-size: 22px;
        width: 100%;
    }

    /* CONTEÚDO */
    .content {
        width: 100%;
        padding: 5rem 4rem;
